import * as React from 'react';
import axios from 'axios';
import { useState, useEffect } from 'react';
import Dialog from '@mui/material/Dialog';
import Card from '@mui/material/Card';

import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import { Typography } from '@material-ui/core';
import CardActions from '@mui/material/CardActions';
import EditIcon from '@mui/icons-material/Edit';
import { IconButton } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import Box from '@mui/material/Box';
import {
  ChatBubbleOutline,
  Favorite,
  FavoriteBorder,
} from '@mui/icons-material';

import DeleteIcon from '@mui/icons-material/Delete';
import { deletePost } from '../features/Posts/postSlice';
import { editPost } from '../features/Posts/postSlice';
import { Button, stepConnectorClasses } from '@mui/material';
import { addComment } from '../features/Posts/postSlice';

import CommentCard from './commentCard/commentCard';

export default function PostItem({ post }) {
  const { user } = useSelector((state) => state.auth);

  const [liked, setLiked] = useState(post.likes.includes(user._id));
  const [noOfLikes, setNo] = useState(post.likes.length);

  const [commentValue, setCommentValue] = useState('');
  const [commentToggle, setCommentToggle] = useState(false);

  //  const[isDelete,setIsDelete]=useState(false);
  //  const[isAccount,setIsAccount]=useState(false);

  //  console.log(post);
  //  console.log(post.likes.length);
  const dispatch = useDispatch();

  const userId = user._id;
  const token = user.token;
  // console.log(userId,token);
  const postId = post._id;
  // console.log(postId);
  const handleLike = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/posts/${post._id}`,
        {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          // credentials: 'include',
        }
      );

      if (response.ok) {
        setLiked(true);
        setNo(noOfLikes + 1);
      }
    } catch (error) {
      console.log(error);
    }
  };
  const handleUnlike = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/posts/${post._id}`,
        {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          //  credentials: 'include',
        }
      );

      if (response.ok) {
        setLiked(false);
        setNo(noOfLikes - 1);
      }
    } catch (error) {
      console.log(error);
    }
  };



  const addCommen = async (comment) => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/posts/comment/${post._id}`,
        {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          // credentials: 'include',
          body: JSON.stringify(comment),
        }
      );
      return response.json();
    } catch (error) {
      console.log(error);
    }
  };
  // const addCommentHandler = async(e) => {
  //   e.preventDefault();
  //  await dispatch(addComment(postId,commentValue));

  //   if (isAccount) {
  //     dispatch(getMyPosts());
  //   } else {
  //     dispatch(getFollowingPosts());
  //   }
  // };

  


  return (
    <Box>
      <Card
        sx={{
          mb: '1vmax',
          boxShadow: '3',
        }}
      >
        <CardHeader title={post.title} subheader={post.creator} />
        <Typography variant='subheader' color='text.secondary'>
          {new Date(post.createdAt).toLocaleString()}
        </Typography>

        <Typography sx={{ fontSize: 14 }} color='text.secondary'>
          {post.location}
        </Typography>
        <CardMedia
          component='img'
          sx={{ heigth: 140 }}
          image={post.selectedFile}
          alt='Paella dish'
        />
        <CardContent>
          <Typography variant='body2' color='text.secondary'>
            {post.message}
          </Typography>
        </CardContent>
        <CardActions disableSpacing>
          <Button>
            {liked ? (
              <Favorite style={{ color: 'red' }} onClick={handleUnlike} />
            ) : (
              <FavoriteBorder style={{ color: 'grey' }} onClick={handleLike} />
            )}
          </Button>
          <Typography>{noOfLikes}</Typography>

          <Button onClick={() => setCommentToggle(!commentToggle)}>
            <ChatBubbleOutline />
          </Button>

          <Dialog
            open={commentToggle}
            onClose={() => setCommentToggle(!commentToggle)}
          >
            <div style={{ minwidth: 500, height: '100vh', padding: '2vmax' }}>
              <Typography variant='h4'>Comments</Typography>
              <form style={{ display: 'flex ',margin: '1vmax'}} onSubmit={addCommentHandler}>
                <input
                  type='text'
                  value={commentValue}
                  onChange={(e) => setCommentValue(e.target.value)}
                  placeholder='Comment Here...'
                  required
                  style={{
                    width: '100%',
  padding:' 1vmax 2vmax',
  outline:' none',
  border: '1px solid rgba(0, 0, 0, 0.288)',
  borderRadius: '30px 0 0 30px',
  font:' 100 1vmax "Roboto", sans-serif',
                  }}
                />

                <Button type='submit' variant='contained'>
                  Add
                </Button>
              </form>

              {post.comments.length > 0 ? (
                post.comments.map((item) => (
                  <CommentCard
                    userId={item.user._id}
                    name={item.user.name}
                    avatar={item.user.avatar.url}
                    comment={item.comment}
                    commentId={item._id}
                    key={item._id}
                    postId={postId}
                    // isAccount={isAccount}
                  />
                ))
              ) : (
                <Typography>No comments Yet</Typography>
              )}
            </div>
          </Dialog>

          {/*  post's userid is same as login id then show delete otherwise dont  */}

          {user._id === post.user ? (
            <IconButton aria-label='delete'>
              <DeleteIcon onClick={() => dispatch(deletePost(post._id))} />
            </IconButton>
          ) : null}

          {user._id === post.user ? (
            <IconButton aria-label='delete'>
              <EditIcon onClick={() => dispatch(editPost(post._id))} />
            </IconButton>
          ) : null}
        </CardActions>
      </Card>
    </Box>
  );
}
