import React,{useState,useEffect}from 'react';
import { Link } from 'react-router-dom';
import './CommentCard.css';
import { Delete } from '@mui/icons-material';

import { useDispatch, useSelector } from 'react-redux';
import { getUserFeed } from '../../features/Posts/postSlice';
// import { deleteCommentOnPost } from '../../Actions/Post';
// import { getFollowingPosts, getMyPosts } from '../../Actions/User';
// import getFollowingPosts from '.';
import { deleteComment } from '../../features/Posts/postSlice';
const CommentCard = ({
  userId,  // the person who commented 
  name,
// profilePicture,
  comment,
  commentId,
  postId,
  // isAccount,
  token,
  postUser,
}) => {
  const [userInComment, setUserInComment] = useState(null);
  // const { user } = useSelector((state) => state.user);
  // const token=user.token;
  const dispatch = useDispatch();

  const deleteCommentHandle = async() => {
    dispatch(deleteComment(postId, commentId));

    // if (isAccount) {
    //   dispatch(getMyPosts());
    // } else {
    await  dispatch(getUserFeed());
    // }
  };


useEffect(()=>{
getData(userId)
// .then((userData)=>{
//    console.log(userData)
//   setUserInComment(userData);
// })
// .catch((error)=>{
//   console.error(" error fetching usrer",error);
// })
},[userId,token]);


  const getData = async () => {
    const res = await fetch(`http://localhost:5000/api/users/${userId}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    const user = await res.json();
    setUserInComment(user);
    console.log(user);
  };

  if (!userInComment) {
    // User data is loading or not available yet
    return <div>Loading...</div>;
  }
 












    
     console.log(userId);
  return (
    <div className='commentUser'>
      <img src={userInComment.profilePicture} alt={userInComment.name} />
      <Typography
        style={{ minWidth: '6vmax', fontWeight: 'bold', color: 'black' }}
      >
        {userInComment.name}
      </Typography>

      <Typography>{comment}</Typography>

      {userId === postUser ? (
        <Button onClick={deleteCommentHandle}>
          <Delete />
        </Button>
      ) : userId === user._id ? (
        <Button onClick={deleteCommentHandle}>
          <Delete />
        </Button>
      ) : null}
      {/* { userId === postUser ? (
        <Button onClick={deleteCommentHandle}>
          <Delete />
        </Button>
      ) : null} */}
    </div>
  );
};

export default CommentCard;
