import  React from 'react';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card'
import TextField from '@mui/material/TextField';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import CardHeader from '@mui/material/CardHeader';
import { useDispatch } from 'react-redux';
import {createPost} from '../features/Posts/postSlice'
import{useState} from 'react'





function PostForm() {

   const [postData, setpostData] = useState({
  creator:'',
  title:'',
  message: '',
  tags:'',
   });


const{creator,title,message,tags}=postData;

const dispatch=useDispatch()



// const { posts, isLoading, isError, isSuccess } = useSelector(
//   (state) => state.posts
// );






// const onChange=(e)=>{


//   setpostData((prevState) => ({
//       ...prevState,
//       [e.target.name]: e.target.value,
//     }));

// }

const submit=(e)=>{
 e.preventDefault();
  console.log("clicked submit button")
// const postData = {
//         name,
//         email,
//         password,
//       };
  dispatch(createPost(postData))
  console.log(postData);
  setpostData({creator: '',
  title: '',
  message: '',
  tags:''})
};

  return (
    <>
      <Card sx={{ m: 5, width: 300, height: 480 }}>
        <CardHeader title=' Create your memories...' />
        <CardContent>
          <Box
            component='form'
            sx={{
              // '& .MuiTextField-root': { m: 1, width: '25ch' },
              width: 300,
              height: 600,
              m: 5,
            }}
            noValidate
            autoComplete='off'
          >
            <div sx={{ mt: 2 }}>
              <TextField
                required
                id='outlined-Creator-input'
                label='Creator'
                sx={{ mb: 1, mt: -7 }}
                value={postData.creator}
                onChange={(e) => this.setpostData(e.target.value)}
              />
              <TextField
                id='outlined-Title-input'
                label='Title'
                sx={{ mb: 1 }}
                value={title}
                // onChange={onChange}
              />
              <TextField
                id='outlined-Message-input'
                label='Message'
                value={message}
                inputProps={{
                  style: {
                    height: '40px',
                  },
                }}
                sx={{ mb: 1 }}
                // onChange={onChange}
              />
              <TextField
                id='outlined-Tags-input'
                label='Tags'
                sx={{ mb: 1 }}
                value={tags}
                // onChange={onChange}
              />
            </div>
            <div>
              <Button variant='outlined' sx={{ mb: 1 }}>
                Choose file..
              </Button>
            </div>
            <div>
              <Button
                variant='contained'
                color='success'
                sx={{ mb: 1, width: 200 }}
                onClick={submit}
              >
                Submit
              </Button>
            </div>

            <div>
              <Button
                variant='contained'
                color='error'
                sx={{ mb: 1, width: 200 }}
              >
                clear
              </Button>
            </div>
          </Box>
        </CardContent>
      </Card>
    </>
  );
}
export default PostForm
