import * as React from 'react';
import {Link,useNavigate} from 'react-router-dom'
import AppBar from '@mui/material/AppBar';
import { theme } from '../components/styling'
import{useState} from 'react';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import { Menu } from '@mui/icons-material';
import FlexBetween from './FlexBetween'
import {useSelector,useDispatch} from 'react-redux';
import{logout,reset} from '../features/auth/authSlice'
import { useMediaQuery } from '@mui/material';
import { useSelect } from '@mui/base';
import { ThemeProvider, } from '@mui/material/styles';
import { green } from '@mui/material/colors';





export default function Header() {
 const navigate=useNavigate()
 const dispatch=useDispatch()
 
 // from state we only want user so we use useselector 
 const {user}=useSelector((state)=>state.auth);
// const [mode, setMode] = useState([]);
const [isMobileMenuToggled,setIsMobileMenuToggled]=useState(false);
 const isNonMobileScreens=useMediaQuery("(min-width:600px)");
const onLogout=()=>{
  console.log(" clicked on logout button")
  dispatch(logout())
  dispatch(reset())
  navigate('/login')
}


  return (
    <FlexBetween padding=' 0.5rem 6%' backgroundColor={'green'}>
      <FlexBetween gap='1.75rem'>
        <Typography fontWeight='bold' fontSize='clamp(1rem,2rem,2.25rem)'>
          <Link to='/' style={{ textDecoration: 'none' }}>
            Memories
          </Link>
        </Typography>
      </FlexBetween>

      {/* desktop version  */}
      {isNonMobileScreens ? (
        <FlexBetween>
          {user ? (
            <>
              <Button sx={{ mr: 2 }} onClick={onLogout}>
                Logout
              </Button>

              <Link to='/MyPosts'>
                <Button>My Post</Button>
              </Link>
            </>
          ) : (
            <>
              <Link to='/Register'>
                <Button>Register</Button>
              </Link>
              <Link to='/Login'>
                <Button>Login</Button>
              </Link>
            </>
          )}
        </FlexBetween>
      ) : (
        <IconButton
          onClick={() => setIsMobileMenuToggled(!isMobileMenuToggled)}
        >
          <Menu />
        </IconButton>
      )}

      {}

      {/*  version  */}
    </FlexBetween>
  );
}
