// Favorites.js

import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getPosts } from '../features/Posts/postSlice';
import { removeFavorite } from '../features/fav/favSlice';

const Favorites = () => {

  const dispatch = useDispatch();
  const favoritePostIds = useSelector((state) => state.favorites.postIds);
  const favoritePosts = useSelector((state) =>
    state.posts.items.filter((post) => favoritePostIds.includes(post._id))
  );

  useEffect(() => {
    dispatch(getPosts());
  }, [dispatch]);

  const handleRemoveFavorite = (postId) => {
    dispatch(removeFavorite(postId));
  };

  return (
    <div>
      <h2>My Favorite Posts</h2>
      <ul>
        {favoritePosts.map((post) => (
          <li key={post._id}>
            <h3>{post.title}</h3>
            <p>{post.body}</p>
            <button onClick={() => handleRemoveFavorite(post._id)}>
              Remove from Favorites
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Favorites;
