import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import PostForm from '../components/PostForm';
import PostItem from '../components/PostItem';
import Header from '../components/Header'
import { getFeedPosts} from '../features/Posts/postSlice';

import { reset } from '../features/auth/authSlice';
import MyProfile from '../components/MyProfile';
import { Box, Card, Typography } from '@material-ui/core';
import { flexbox } from '@mui/system';




function Dashboard() {

  const navigate = useNavigate();
  const dispatch = useDispatch();

  const { user } = useSelector((state) => state.auth);
    // const [post, setPost] = useState([]);
  const { posts, isLoading, isError, message } = useSelector(
    (state) => state.posts
  );
  console.log(user._id,user.token);



  // useEffect(() => {
  //   console.log(2)
  //   if (isError) {
  //     console.log(message);
  //   }

  //   if (!user) {
      
  //     navigate('/login');
  //   }

  //   dispatch(getFeedPosts());

  //   return () => {
  //     dispatch(reset());
  //   };
  // }, [user,navigate, isError, message, dispatch]);

 const userId = user._id;
 const token = user.token;
 useEffect(() => {
   getUserFeed();
 }, [userId, token]);



  const getUserFeed = async () => {
    const res = await fetch(
      `http://localhost:5000/api/posts`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    const post = await res.json();
    // setPost(posts.posts);
    posts=posts
    console.log(post);
  };










  return (
    <Box>
      <Header />
      <Box
        width='100vh'
        padding='2rem'
        gap='0.5rem'
        justifyContent='space-between'
        display='flex'
      >
        <Box>
          <PostForm />
        </Box>

        <Box sx={{ padding: '3rem', width: 500, alignContent: 'center' }}>
          {posts.length > 0 ? (
            <Box sx={{ width: 500, height: 300 }}>
              {posts.map((post) => (
                <PostItem key={post._id} post={post} />
              ))}
            </Box>
          ) : (
            <Typography>You have not set any memories</Typography>
          )}
        </Box>

        <Box sx={{ ml: '6rem' }}>
          {user ? (
            <MyProfile key={user._id} userId={user._id} token={user.token} />
          ) : null}
        
        </Box>
      </Box>
    </Box>
  );
}

export default Dashboard;
