import { Card } from '@mui/material'
import { Button, CardHeader } from '@material-ui/core';
import{ Box} from '@mui/material';
import {Typography} from '@material-ui/core';
import {Paper} from '@material-ui/core';
import CardMedia from '@mui/material/CardMedia';
import React from 'react'
import { useSelector } from 'react-redux';
function UserItem({person}) 


{
 const { user } = useSelector((state) => state.auth);
  // const userId = loggedInUser._id;
  const token = user.token;
  console.log(user._id);
  
  const handleClick = () => {
    console.log('follow button is clicked ');
    console.log(person._id);
     followUser();
  };

   const followUser=async()=>{
    const res = await fetch(
      `http://localhost:5000/api/users/follow/${person._id}`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
      const data=await res.json();
      console.log(data);
    
   }

  return (
    <Box
      sx={{
        maxWidth: 300,
        pl: '5rem',
        display: 'flex',
        flexDirection: 'row',
        backgroundColor: '#E5E4E2',
      }}
    >
      <Box sx={{ pb: '1rem', Height: 100, mb: '1rem' }}>
        <CardMedia
          sx={{
            height: 40,
            width: 40,
            borderRadius: 100,
            borderColor: 'blue',
            mt: 1,
          }}
          component='img'
          height='50'
          image={person.profilePicture}
          alt='Paella dish'
        />

        <Typography variant='h6' sx={{ ml: '1rem' }}>
          {person.name}
        </Typography>

        <Typography>{person.location}</Typography>

        <Button variant='contained' onClick={handleClick}>
          Follow
        </Button>
      </Box>
    </Box>
  );
}

export default UserItem
