import * as React from 'react';
import {Link,useNavigate} from 'react-router-dom'
import AppBar from '@mui/material/AppBar';
import { theme } from '../components/styling'
import { useStyles } from './styling';
import{useState} from 'react';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import MenuItem from '@mui/material/MenuItem';
// import Button from '@mui/material/Button';
// import Select from '@mui/material/Select';
import MenuIcon from '@mui/icons-material/Menu';
import IconButton from '@mui/material/IconButton';
import InputBase from '@mui/material/InputBase';
import FormControl from '@mui/material/FormControl';
import Close  from '@mui/icons-material/Close';
// import  Menu  from '@mui/icons-material/Menu';
import Menu from '@mui/material/Menu';
import FlexBetween from './FlexBetween'
import {useSelector,useDispatch} from 'react-redux';
import{logout,reset} from '../features/auth/authSlice'
import { useMediaQuery } from '@mui/material';
import { useSelect } from '@mui/base';
import { ThemeProvider, } from '@mui/material/styles';
import { green } from '@mui/material/colors';
import zIndex from '@material-ui/core/styles/zIndex';





export default function Header() {

const classes = useStyles();

 const navigate=useNavigate()
 const dispatch=useDispatch()
 
 // from state we only want user so we use useselector 
 const {user}=useSelector((state)=>state.auth);
// const [mode, setMode] = useState([]);
// const [isMobileMenuToggled,setIsMobileMenuToggled]=useState(false);
 const isNonMobileScreens=useMediaQuery("(min-width:960px)");
const onLogout=()=>{
  // console.log(" clicked on logout button")
  dispatch(logout())
  // dispatch(reset())
  navigate('/login')
}


 const [anchorel, setAnchorel] = useState(null);
 const open = Boolean(anchorel);
 const handleClick = (event) => {
   setAnchorel(event.currentTarget);
 };
 const handleClose = () => {
   setAnchorel(null);
 };




  return (
    <div className={`${classes.header}`}>
      {/* <FlexBetween gap='1.75rem'> */}
      <Typography
        // //  fontSize='clamp(1rem,2rem,2.25rem)'

        style={{
          fontFamily: 'Amatic SC',
          fontWeight: 'bold',
          fontSize: '2.8rem',
          paddingLeft: '1rem',
        }}
      >
        <Link to='/' style={{ textDecoration: 'none' }}>
          Memorify
        </Link>
      </Typography>
      {/* </FlexBetween> */}

      {/* desktop version  */}
      {isNonMobileScreens ? (
        <div>
          {user ? (
            <>
              <a sx={{ mr: 2 }} onClick={onLogout}>
                Logout
              </a>

              <Link to='/MyPosts' style={{ textDecoration: 'none' }}>
                My Post
              </Link>
            </>
          ) : (
            <>
              <Link to='/Register' style={{ textDecoration: 'none' }}>
                <button>Register</button>
              </Link>
              <Link to='/Login' style={{ textDecoration: 'none' }}>
                <button>Login</button>
              </Link>
            </>
          )}
        </div>
      ) : (
        // <IconButton
        //   onClick={() => setIsMobileMenuToggled(!isMobileMenuToggled)}
        // >
        //   <Menu />
        // </IconButton>

        <>
          <button
            id='demo-positioned-button'
            aria-controls={open ? 'demo-positioned-menu' : undefined}
            aria-haspopup='true'
            aria-expanded={open ? 'true' : undefined}
            onClick={handleClick}
          >
            <MenuIcon />
          </button>
          <Menu
            id='demo-positioned-menu'
            aria-labelledby='demo-positioned-button'
            anchorel={anchorel}
            open={open}
            onClose={handleClose}
            anchorOrigin={{
              vertical: 'top',
              horizontal: 'right',
            }}
            transformOrigin={{
              vertical: 'top',
              horizontal: 'right',
            }}
          >
            <MenuItem component='a' href='/MyProfile'>
              Profile
            </MenuItem>

            <MenuItem component='a' href='/MyPosts'>
              My Posts
            </MenuItem>

            <MenuItem onClick={onLogout}>Logout</MenuItem>
          </Menu>
        </>
      )}

      {/* {!isNonMobileScreens && isMobileMenuToggled && (
       */}
      {/* <Button
            id='basic-button'
            aria-controls={open ? 'basic-menu' : undefined}
            aria-haspopup='true'
            aria-expanded={open ? 'true' : undefined}
            onClick={handleClick}
          >
            Dashboard
          </Button> */}

      {/* <Menu
            id='demo-positioned-menu'
            aria-labelledby='demo-positioned-button'
            anchorel={anchorel}
            open={open}
            onClose={handleClose}
            anchororigin={{
              vertical: 'top',
              horizontal: 'left',
            }}
            transformorigin={{
              vertical: 'top',
              horizontal: 'left',
            }}
            sx={{zIndex:'10'}}
          > */}
      {/* </div>
      )} */}

      {/*  version  */}
    </div>
  );
}
