import * as React from 'react';
import {Link,useNavigate} from 'react-router-dom'
import AppBar from '@mui/material/AppBar';
import { theme } from '../components/styling'
import{useState} from 'react';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import MenuItem from '@mui/material/MenuItem';
import Button from '@mui/material/Button';
// import Select from '@mui/material/Select';

import IconButton from '@mui/material/IconButton';
import InputBase from '@mui/material/InputBase';
import FormControl from '@mui/material/FormControl';
import Close  from '@mui/icons-material/Close';
import  Menu  from '@mui/icons-material/Menu';
import FlexBetween from './FlexBetween'
import {useSelector,useDispatch} from 'react-redux';
import{logout,reset} from '../features/auth/authSlice'
import { useMediaQuery } from '@mui/material';
import { useSelect } from '@mui/base';
import { ThemeProvider, } from '@mui/material/styles';
import { green } from '@mui/material/colors';





export default function Header() {
 const navigate=useNavigate()
 const dispatch=useDispatch()
 
 // from state we only want user so we use useselector 
 const {user}=useSelector((state)=>state.auth);
// const [mode, setMode] = useState([]);
// const [isMobileMenuToggled,setIsMobileMenuToggled]=useState(false);
 const isNonMobileScreens=useMediaQuery("(min-width:600px)");
const onLogout=()=>{
  console.log(" clicked on logout button")
  dispatch(logout())
  dispatch(reset())
  navigate('/login')
}


 const [anchorEl, setAnchorEl] = React.useState(null);
 const open = Boolean(anchorEl);
 const handleClick = (event) => {
   setAnchorEl(event.currentTarget);
 };
 const handleClose = () => {
   setAnchorEl(null);
 };




  return (
    <FlexBetween padding=' 0.5rem 6%' backgroundColor={'green'}>
      <FlexBetween gap='1.75rem'>
        <Typography fontWeight='bold' fontSize='clamp(1rem,2rem,2.25rem)'>
          <Link to='/' style={{ textDecoration: 'none' }}>
            Memories
          </Link>
        </Typography>
      </FlexBetween>

      {/* desktop version  */}
      {isNonMobileScreens ? (
        <FlexBetween>
          {user ? (
            <>
              <Button sx={{ mr: 2 }} onClick={onLogout}>
                Logout
              </Button>

              <Link to='/MyPosts'>
                <Button>My Post</Button>
              </Link>
            </>
          ) : (
            <>
              <Link to='/Register'>
                <Button>Register</Button>
              </Link>
              <Link to='/Login'>
                <Button>Login</Button>
              </Link>
            </>
          )}
        </FlexBetween>
      ) : (
        // <IconButton
        //   onClick={() => setIsMobileMenuToggled(!isMobileMenuToggled)}
        // >
        //   <Menu />
        // </IconButton>

        <div>
          <Button
            id='basic-button'
            aria-controls={open ? 'basic-menu' : undefined}
            aria-haspopup='true'
            aria-expanded={open ? 'true' : undefined}
            onClick={handleClick}
          >
            Dashboard
          </Button>

          <Menu
            id='demo-positioned-menu'
            aria-labelledby='demo-positioned-button'
            anchorEl={anchorEl}
            open={open}
            onClose={handleClose}
            anchorOrigin={{
              vertical: 'top',
              horizontal: 'left',
            }}
            transformOrigin={{
              vertical: 'top',
              horizontal: 'left',
            }}
          >
            <MenuItem onClick={handleClose}>Profile</MenuItem>
            <MenuItem onClick={handleClose}>My account</MenuItem>
            <MenuItem onClick={handleClose}>Logout</MenuItem>
          </Menu>
        </div>
      )}

      {/* {!isNonMobileScreens && isMobileMenuToggled && (
        
      )} */}

      {/*  version  */}
    </FlexBetween>
  );
}
