    <div>
      <Header />
      <div>
        <form
          style={{
            marginLeft: '30%',
            height: ' 400px',
            backgroundColor: ' 	#E0E0E0',
          }}
          onSubmit={submitHandler}
        >
          <h3>Update Profile</h3>
          <input
            type='text'
            value={name}
            placeholder='name'
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type='text'
            value={email}
            placeholder='name'
            onChange={(e) => setEmail(e.target.value)}
          />
          <Button type='submit'>Update</Button>
        </form>
      </div>
    </div>
  ); 
}

export default UpdateProfile
