import * as React from 'react';

import { useState,useEffect } from 'react';

import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import { Typography } from '@material-ui/core';
import CardActions from '@mui/material/CardActions';

import {IconButton }from '@material-ui/core';
import { useDispatch ,useSelector} from 'react-redux';

import {Favorite,FavoriteBorder} from '@mui/icons-material'

import DeleteIcon from '@mui/icons-material/Delete';
import { deletePost } from '../features/Posts/postSlice';
import { Button, stepConnectorClasses } from '@mui/material';


export default function PostItem({post}) {
   const { user } = useSelector((state) => state.auth);
   const[liked,setLiked]=useState(post.likes.includes(user._id));

 const dispatch=useDispatch();

  const userId=user._id;
  const token=user.token;
  console.log(userId,token);
  const postId=post._id

 const handleLike=async()=>{
    try {
      await axios.post(`/api/posts/${post._id}/like`, {}, { withCredentials: true });
      setLiked(true);
    } catch (error) {
      console.log(error);
    }
    // dispatch(likeUnlikePost())
 }

  const handleUnlike = async () => {
    try {
      await axios.delete(`/api/posts/${post._id}/like`, {
        withCredentials: true,
      });
      setLiked(false);
    } catch (error) {
      console.log(error);
    }
  };
// useEffect(()=>{
//    likeUnlikePost()
     
  
// },[userId],token)

// const likeUnlikePost=async()=>{
//   const res=await fetch(`http://localhost:5000/api/posts/${postId}`,{
//     header:{
//       Authorization:`Bearer ${token}`,
//     },
//   })
//   const data=await res.json()
//   console.log(data);
// setLike(data)
// setCounter(counter+1);
// }








  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardHeader title={post.title} subheader={post.creator} />
      <Typography variant='subheader' color='text.secondary'>
        {new Date(post.createdAt).toLocaleString()}
      </Typography>

      <Typography sx={{ fontSize: 14 }} color='text.secondary'>
        {post.location}
      </Typography>
      <CardMedia
        component='img'
        sx={{ heigth: 140 }}
        image={post.selectedFile}
        alt='Paella dish'
      />
      <CardContent>
        <Typography variant='body2' color='text.secondary'>
          {post.message}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <Typography>5likes</Typography>
        <Button>
          {liked ? (
            <Favorite style={{ color: 'red' }} onClick={handleLike} />
          ) : (
            <FavoriteBorder style={{ color: 'grey' }} onClick={handleUnlike} />
          )}
        </Button>

        <IconButton aria-label='delete'>
          <DeleteIcon onClick={() => dispatch(deletePost(post._id))} />
        </IconButton>
      </CardActions>
    </Card>
  );
}
